CREATE TABLE `gottobethere` (
  `titulo` varchar(60) NOT NULL,
  `duracion` varchar(6) NOT NULL,
  `numero` int(2) NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`numero`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;




CREATE TABLE `grupo` (
  `nombre` varchar(20) NOT NULL,
  `descripcion` varchar(10000) NOT NULL,
  `foto` varchar(1000) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;




CREATE TABLE `history` (
  `titulo` varchar(60) NOT NULL,
  `duracion` varchar(6) NOT NULL,
  `numero` int(2) NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`numero`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;




CREATE TABLE `log` (
  `num_id` int(11) NOT NULL AUTO_INCREMENT,
  `evento` varchar(1000) NOT NULL,
  PRIMARY KEY (`num_id`)
) ENGINE=InnoDB AUTO_INCREMENT=212 DEFAULT CHARSET=utf8;




CREATE TABLE `pedidos` (
  `disco_solicitado` varchar(50) NOT NULL,
  `precio_disco` float NOT NULL,
  `estado_pedido` varchar(20) NOT NULL DEFAULT 'pendiente',
  `nombre_comprador` varchar(40) NOT NULL,
  `apellidos_comprador` varchar(80) NOT NULL,
  `email_comprador` varchar(80) NOT NULL,
  `telefono_comprador` int(11) NOT NULL,
  `direccion_comprador` varchar(120) NOT NULL,
  `metodo_pago` varchar(20) NOT NULL,
  `numero_tarjeta` int(20) NOT NULL,
  `mes_tarjeta` varchar(12) NOT NULL,
  `año_tarjeta` int(4) NOT NULL,
  `codigo_tarjeta` int(3) NOT NULL,
  `fecha_pedido_procesado` date NOT NULL,
  `texto_enviar` varchar(5000) NOT NULL,
  `usuario_gestor_pedido` varchar(20) NOT NULL,
  `id_pedido` int(11) NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id_pedido`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;




CREATE TABLE `prehistory` (
  `titulo` varchar(60) NOT NULL,
  `duracion` varchar(6) NOT NULL,
  `numero` int(2) NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`numero`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;




CREATE TABLE `thebadmixes` (
  `titulo` varchar(60) NOT NULL,
  `duracion` varchar(6) NOT NULL,
  `numero` int(2) NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`numero`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;




CREATE TABLE `usuarios` (
  `DNI` varchar(9) NOT NULL,
  `usuario` varchar(20) NOT NULL,
  `clave` varchar(20) NOT NULL,
  `email` varchar(40) NOT NULL,
  `tipo` varchar(20) NOT NULL,
  `nombre` varchar(20) NOT NULL,
  `apellidos` varchar(40) NOT NULL,
  PRIMARY KEY (`usuario`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


